/* Sampler, built Thu May 28 2015 22:47:09 */var Helper = {
	toObject: function(arr) {
		var rv = {};
		
		for (var i = 0; i < arr.length; ++i) {
			if (arr[i] !== undefined) rv[i] = arr[i];	
		}
		
		return rv;
	}
}
var app = angular.module('Subscriber', ['ngRoute']);

app.config(function($routeProvider) {
	$routeProvider.
	when('/', {templateUrl: '/public/html/views/main.html'}).
	when('/view', {templateUrl: '/public/html/views/list.html', controller: 'uploadController'}).
	when('/upload', {templateUrl: '/public/html/views/upload.html', controller: 'uploadController'});
});
// Main Controller
app.controller('mainController', function($scope, $http) {	

});
// Upload CSV controller
app.controller('uploadController', function($scope, $http) {
	$scope.headings = [];
	$scope.records = [];
	
	$scope.validFileType = function(fileName) {
		var valid = ['csv', 'CSV'];
		var ext = fileName.split('.').pop();
		
		return (valid.indexOf(ext) !== false) ? true : false;
	};
	
	// Parse CSV file
	$scope.parseFile = function(files) {
		var reader = new FileReader();
		
		// Take the first selected file
		$scope.file = files[0];
		
		// Get filename
		$scope.fileName = files[0].name;
		
		// Check valid file type
		if(!$scope.validFileType($scope.fileName)) {
			console.log('Invalid file type');
			return false;
		}
		
		// Using closures and anonymous self invoking function
		// so that we can use scope with the File Reader API
		reader.onloadend = (function($scope, dataService) {
			return function(file) {				
				var content = file.target.result;
				
				// Split rows at new line
				var rows = content.split(/\n/);
				
				// Extract headings (split columns at comma)
				$scope.headings = Helper.toObject(rows[0].split(/,/));
				rows.shift();
				
				// Extract records (split columns at comma)
				rows.forEach(function(item, index) {					
					var record = Helper.toObject(item.split(/,/));
					$scope.records.push(record);
				});
				
				// Emit file-upload event on completion of parsing
				/*$scope.$broadcast('file-upload', {
					headings: $scope.headings,
					records: $scope.records
				});*/
				
				console.log($scope.headings, $scope.records);
			}
		})($scope);
			
		reader.readAsText($scope.file);
	};
});